        return x % 5


class Rectangle(Shape):
    def __init__(self, length, width):
        self.length = length
        self.width = width

    def area(self):
        new_length = super().randomize(self.length)
        new_width = self.randomize(self.width)
        return new_length * new_width

    def randomize(self, x):
        return x % 2


